import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { DataGrid } from '@mui/x-data-grid';
function createData(quantity,productName, productPrice ) {
    return { quantity,productName, productPrice };
  }

// QUery a la base de dades quan es carregui la imatge , mentrestant es fa servir aixo 
let classes = ['quantity', 'productName', 'ProductPrice']
const rows = [
    createData( 159,'Frozen yoghurt', 6.0),
    createData( 159,'Ice cream sandwich', 237),
    createData( 159,'Eclair', 262),
    createData( 159,'Cupcake', 305),
    createData( 159, 'Gingerbread', 356)
  ];

export default function BasicTable() {
    /*return (
      <TableContainer component={Paper}>
